import React,{useState}from "react";
import {addTodo,removeAllTodo,deleteTodo} from "../actions/index";
import {useSelector,useDispatch} from "react-redux";
import e from "cors";



function Todo() {

    const [inputData,setInputData] = useState('')
    const dispatch = useDispatch();

    const list = useSelector((state)=>state.todoReducer.list)

  return (
    <>
      <div className="main-div">
        <div className="child-div">
          <figere>
            <figcaption>Todo List</figcaption>
          </figere>
          <div className="addItems">
            <input type="text" placeholder="Enter your name" value={inputData} onChange={(e)=>{setInputData(e.target.value)}} />
            <button onClick={()=>dispatch(addTodo(inputData),setInputData(''))}><i class="fa fa-plus"></i></button>
          </div>
          <div className="showItmes" >
              {
                  list.map(()=>{

                <div className="eachItems" style={{display:"flex"}} key={e.id}>
                    <h3>Items</h3>
                    <button onClick={()=>dispatch(deleteTodo(inputData),setInputData(''))}><i class="fa fa-trash" aria-hidden="true"></i></button>

                </div>
                  })
              }
          </div>


        </div>

      </div>
    </>
  );
}

export default Todo;
